        /* showLocationSearch FUNCTION 
        UNHIDES THE CITY SEARCHBOX. USED IF 
        LOCATION RETURNED BY GEOLOCATION IS INCORRECT - ONCLICK EVENT TRIGGER
        OR IF GEOLOCATION DOESN'T WORK ON THE USER'S BROWSER - locOff FUNCTION IN geoLocate FUNCTION.
        */
        function showLocationSearch() {
        document.getElementById("loc_search").style.display = "inherit";
        document.getElementById("openSearch").style.display = "none";
        }
        // END OF showLocationSearch FUNCTION 
      
      function hideLocationSearch() {
        document.getElementById("loc_search").style.display = "none";
        document.getElementById("openSearch").style.display = "inherit";
        }
      
      
       /* getWeather FUNCTION 
        FUNCTION TO MAKE AJAX CALL TO RETURN WEATHER OBJECT. URL PARAMETER DEPENDS ON WHETHER CALL MADE BASED ON COORDINATES OR CITY NAME.
        */
      function getWeather(url, callback) {
        var request,
            result;
        if (window.XMLHttpRequest) {
          // code for modern browsers
          request = new XMLHttpRequest();
        } 
        else {
          // code for IE6, IE5
          request = new ActiveXObject("Microsoft.XMLHTTP");
        }
        request.open("GET", url, true);
        request.onreadystatechange = function() {
          if (request.readyState < 4)                         // while waiting response from server
              document.getElementById("displayCity").innerHTML = "Loading...";
          else if (request.readyState == 4 && request.status == 200) {  // if successful response from server
            result = callback(request);
          }
        };
        request.send();
        console.log("result in getWeather func:" + result);
        return result;
      }
      
