        function toggleTempType() {
            var temp = document.getElementById("temp").innerHTML,
                thermActive = document.getElementById("thermActive"),
                thermInactive = document.getElementById("thermInactive"),
                degC = document.getElementById("degC"),
                degF = document.getElementById("degF"),
                toFarenheit = function(celcius) {
                  var farenheit = (celcius * 9/5) + 32;
                  farenheit = +farenheit.toFixed(2);
                  thermActive.innerHTML = icons.thermF.svg;
                  thermInactive.innerHTML = icons.thermC.svg;
                  degF.className = "activeDeg";
                  degC.className = "inactiveDeg";
                  document.getElementById("temp").innerHTML = farenheit;
                  return farenheit;
                },
                toCelcius = function(farenheit) {
                  var celcius = (farenheit - 32) * 5/9;
                  celcius = +celcius.toFixed(2);
                  thermActive.innerHTML = icons.thermC.svg;
                  thermInactive.innerHTML = icons.thermF.svg;
                  degC.className = "activeDeg";
                  degF.className = "inactiveDeg";
                  document.getElementById("temp").innerHTML = celcius;
                  return celcius;
                };
            
            if (thermActive.children[0]["id"]=="thermomC") {
              toFarenheit(temp);
              console.log("degC to degF");
            }
            else {
              toCelcius(temp);
              console.log("degF to degC");
            }
          }
      